
public class Patterns {

	public static void main(String[] args) {
		System.out.println("26 march 2024");
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) 
	{
			for(int j=1;j<=i;j++) {
				System.out.print("*"+ " ");
			}System.out.println();
		}
		System.out.println("=============");
		
// decreasing order
		
		for(int i=1;i<=5;i++) 
		{
				for(int j=5;j>=i;j--) {
					System.out.print(i+ " ");
				}System.out.println();
			}
		System.out.println("pppppppp");	
		for(int i=1;i<=5;i++) 
		{
				for(int j=1;j<=i;j++) {
					System.out.print(i+ " ");
				}System.out.println();
			}
		
	}

}
